package com.example.hw3_2_m3;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.hw3_2_m3.databinding.FragmentFirstBinding;

import java.util.ArrayList;

public class FirstFragment extends Fragment {
    private FragmentFirstBinding binding;
    private ArrayList<Air> airs = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        binding = FragmentFirstBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        AirAdapter adapter = new AirAdapter(airs);
        binding.rvAirs.setAdapter(adapter);
        initAdapter();
    }

    private void initAdapter() {
        airs.add(new Air("Plane", "data:image/png;base64,", "In the history of flight, the most important landmarks and events include an understanding of the dynamic reaction of lifting surfaces (or wings), building absolutely reliable engines that produce sufficient power to propel an airframe, and solving the problem of flight control in three dimensions. At the start of the 20th century, the Wright brothers demonstrated that the basic technical problems associated with heavier-than-air flying machines had been overcome, and military and civil aviation developed quickly afterward."));
        airs.add(new Air("Balloon", "data:image/jpeg;base64,", "Возду́шный шар — летательный аппарат (аэростат), использующий для полёта газ, который легче воздуха. Состоит из заполненной газом оболочки и прикреплённой к ней корзины или кабины для пилота и пассажиров. В отличие от дирижаблей, воздушные шары не имеют двигателей для самостоятельного горизонтального движения в воздухе. В зависимости от наполнения различают монгольфьеры (шары, наполненные нагретым воздухом), шарльеры (наполнены лёгким газом, как правило, водородом или гелием) и розьеры (воздушные суда, использующие одновременно газ и воздух, размещённые в отдельных оболочках)"));
        airs.add(new Air("Airbus", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTG9jbiJP776ZcrvBoMu_oyk6Z3Ry5rltnAOA&s", "In the history of flight, the most important landmarks and events include an understanding of the dynamic reaction of lifting surfaces (or wings), building absolutely reliable engines that produce sufficient power to propel an airframe, and solving the problem of flight control in three dimensions. At the start of the 20th century, the Wright brothers"));
    }
}